
var imgSrc = '';
var imgNull = 'data:image/png;base64,';
window.onload = function () {
    var ty=new tuya({
        Base64ck:function (data) {
            imgSrc = data;
            /*console.log(imgSrc);*/
            if(imgSrc != imgNull){
                $(".photo-img").css('background-image', 'url(' + imgSrc + ')');
                $(".downImg").attr("src",imgSrc);
            }
            $(".Dialog-cover,.Dialog-wrap").fadeIn(200);
        }
    });
};


$(".photo-save").click(function () {
    $(".downImg-cover,.downImg-wrap").fadeIn(200);
});

$(".downImg-close").click(function () {
    $(".downImg-cover,.downImg-wrap").fadeOut(50);
});

